mapper.insert:
insert into config_class (id, class_name, include
  )
values (#{id,jdbcType=INTEGER}, #{className,jdbcType=VARCHAR}, #{include,jdbcType=VARCHAR}
  )

mapper.update:
update config_class
set class_name = #{className,jdbcType=VARCHAR},
  include = #{include,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, class_name, include
from config_class
where id = #{id,jdbcType=INTEGER}
