set delete_time = #{deleteTime,jdbcType=VARCHAR},
  destroy_time = #{destroyTime,jdbcType=VARCHAR},
  file_id = #{fileId,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

